import { Component } from '@angular/core';
import { NavController } from '@ionic/angular';
import { Location } from '@angular/common';
import { environment } from 'src/environments/environment';

import { initializeApp } from 'firebase/app';
import { getFirestore, collection, getDocs } from 'firebase/firestore';

const firebaseConfig = environment.firebaseConfig;

@Component({
  selector: 'app-home',
  templateUrl: './home.page.html',
  styleUrls: ['./home.page.scss'],
})
export class HomePage {
  private db: any;

  constructor(
    private navCtrl: NavController,
    private location: Location
  ) {
    const firebaseConfig = environment.firebaseConfig;
    const app = initializeApp(firebaseConfig);
    this.db = getFirestore(app);
  }

  public dados: any = [];

  async ngOnInit() {

    this.dados = {
      usuario: await this.consultaCliente(),
      programas: await this.consultaProgramas(),
      conteudos: await this.consultaConteudos(),
      notificacoes: await this.consultaNotificacoes()
    }
    
  }

  async consultaCliente() {
    const consultaCliente = await getDocs(collection(this.db, "usuarios"));

    let usuarios: any = [];

    consultaCliente.forEach((doc) => {
      const data = doc.data();
      usuarios.push(data);
    });

    return usuarios;
  }

  async consultaConteudos() {
    const consultaConteudos = await getDocs(collection(this.db, "conteudos"));

    let conteudos: any = [];

    consultaConteudos.forEach((doc) => {
      const data = doc.data();
      conteudos.push(data);
    });

    return conteudos;
  }

  async consultaProgramas() {
    const consultaProgramas = await getDocs(collection(this.db, "programas"));

    let programas:any = [];

    consultaProgramas.forEach((doc) => {
      const data = doc.data();
      programas.push(data);
    });

    return programas;
  }

  async consultaNotificacoes() {
    const consultaNotificacoes = await getDocs(collection(this.db, "notificacoes"));

    let notificacoes:any = [];

    consultaNotificacoes.forEach((doc) => {
      const data = doc.data();
      notificacoes.push(data);
    });

    return notificacoes;
  }
}
