import { Component, OnInit, Input } from '@angular/core';

@Component({
  selector: 'app-sessao-cards',
  templateUrl: './sessao-cards.component.html',
  styleUrls: ['./sessao-cards.component.scss'],
})
export class SessaoCardsComponent  implements OnInit {
  @Input() tipo: string = '';
  @Input() icone: string = '';
  @Input() treinos: { id: string; imagem: string; nome: string }[] = [];
  @Input() programas: { id: string; imagem: string; nome: string; concluido: boolean }[] = [];

  constructor() { }

  ngOnInit() {}

}
